import React, { useState, useEffect } from 'react';

import ItemDetail from '../ ItemDetail';
//voy a pasar por prop un valor string llamado query que en pocas palabras, es lo que ingreso en el input para que el useEffect se ejecute cada vez que el valor del input cambia
const ItemDetailContainer = ({ query }) => {
  const [products, setProducts] = useState([]);

  const getSearchResults = async () => {
    const response = await fetch(
      `https://api.mercadolibre.com/sites/MLA/search?q=${query}`
    );
    const data = await response.json();
    const { results } = data;
    setProducts(results);
  };

  useEffect(() => {
    getSearchResults();
  }, [query]);

  return (
    <div>
      {products.length ? (
        products.map((product) => {
          return (
            <div key={product.id}>
              <ItemDetail product={product} />
            </div>
          );
        })
      ) : (
        <p>Cargando Productos</p>
      )}
    </div>
  );
};

export default ItemContainer;
